mapper.select:
SELECT
-- 级联表的字段 ,huiyuan.huiyuan_name as huiyuanName ,huiyuan.huiyuan_phone as huiyuanPhone ,huiyuan.huiyuan_id_number as huiyuanIdNumber ,huiyuan.huiyuan_photo as huiyuanPhoto ,huiyuan.huiyuan_email as huiyuanEmail ,huiyuan.new_money as newMoney ,huiyuan.huiyuan_content as huiyuanContent ,zhuanjia.zhuanjia_name as zhuanjiaName ,zhuanjia.zhuanjia_phone as zhuanjiaPhone ,zhuanjia.zhuanjia_id_number as zhuanjiaIdNumber ,zhuanjia.zhuanjia_photo as zhuanjiaPhoto ,zhuanjia.keshi_types as keshiTypes ,zhuanjia.zhiwei_types as zhiweiTypes ,zhuanjia.guahao_money as guahaoMoney ,zhuanjia.zhuanjia_email as zhuanjiaEmail ,zhuanjia.zhuanjia_content as zhuanjiaContent ,zhuanjia.zhuanjia_delete as zhuanjiaDelete ,users.username as uusername ,users.password as upassword ,users.role as urole ,users.addtime as uaddtime
FROM forum a left JOIN huiyuan huiyuan ON a.huiyuan_id = huiyuan.id left JOIN zhuanjia zhuanjia ON a.zhuanjia_id = zhuanjia.id left JOIN users users ON a.users_id = users.id
and a.id in
#{item}
and a.forum_name like CONCAT('%',#{params.forumName},'%')
and ( a.huiyuan_id = #{params.huiyuanId} )
and ( a.zhuanjia_id = #{params.zhuanjiaId} )
and ( a.users_id = #{params.usersId} )
and a.forum_content like CONCAT('%',#{params.forumContent},'%')
and a.super_ids >= #{params.superIdsStart}
and a.super_ids <= #{params.superIdsEnd}
and a.super_ids = #{params.superIds}
and a.forum_types = #{params.forumTypes}
and a.forum_state_types = #{params.forumStateTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.huiyuan_id IS NOT NULL
and huiyuan.huiyuan_name like CONCAT('%',#{params.huiyuanName},'%')
and huiyuan.huiyuan_phone like CONCAT('%',#{params.huiyuanPhone},'%')
and huiyuan.huiyuan_id_number like CONCAT('%',#{params.huiyuanIdNumber},'%')
and huiyuan.huiyuan_email like CONCAT('%',#{params.huiyuanEmail},'%')
and huiyuan.new_money >= #{params.newMoneyStart}
and huiyuan.new_money <= #{params.newMoneyEnd}
and huiyuan.huiyuan_content like CONCAT('%',#{params.huiyuanContent},'%')
and a.zhuanjia_id IS NOT NULL
and zhuanjia.zhuanjia_name like CONCAT('%',#{params.zhuanjiaName},'%')
and zhuanjia.zhuanjia_phone like CONCAT('%',#{params.zhuanjiaPhone},'%')
and zhuanjia.zhuanjia_id_number like CONCAT('%',#{params.zhuanjiaIdNumber},'%')
and zhuanjia.keshi_types = #{params.keshiTypes}
and zhuanjia.zhiwei_types = #{params.zhiweiTypes}
and zhuanjia.guahao_money >= #{params.guahaoMoneyStart}
and zhuanjia.guahao_money <= #{params.guahaoMoneyEnd}
and zhuanjia.zhuanjia_email like CONCAT('%',#{params.zhuanjiaEmail},'%')
and zhuanjia.zhuanjia_content like CONCAT('%',#{params.zhuanjiaContent},'%')
and zhuanjia.zhuanjia_delete >= #{params.zhuanjiaDeleteStart}
and zhuanjia.zhuanjia_delete <= #{params.zhuanjiaDeleteEnd}
and zhuanjia.zhuanjia_delete = #{params.zhuanjiaDelete}
and UNIX_TIMESTAMP(zhuanjia.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(zhuanjia.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.users_id IS NOT NULL
and users.username like CONCAT('%',#{params.username},'%')
and users.password like CONCAT('%',#{params.password},'%')
and users.role like CONCAT('%',#{params.role},'%')
and UNIX_TIMESTAMP(users.addtime) >= UNIX_TIMESTAMP(#{params.addtimeStart})
and UNIX_TIMESTAMP(users.addtime) <= UNIX_TIMESTAMP(#{params.addtimeEnd})
order by a.${params.orderBy} desc
